      revp->down_box(FL_BORDER_BOX);
      revp->color((Fl_Color)14);
      revp->selection_color(FL_FOREGROUND_COLOR);
      revp->labelsize(11);
      revp->textsize(11);
      revp->textcolor(FL_BACKGROUND2_COLOR);
